import { useState, useEffect } from 'react'
import styles from './TemplateEditor.module.css'

const TemplateEditor  = () => {
    // テンプレート文
    const [template, setTemplate] = useState('曲のタイトルは"{title}"です')

    // 動的フィールド（プレースホルダーから自動抽出）
    const [fields, setFields] = useState({})
  
    const [title, setTitle] = useState("")
    const [date, setDate] = useState("")
    const [link, setLink] = useState("")
    const [result, setResult] = useState("")

    const extractPlaceholders = (text : string) => {
        const matches = text.match(/\{([^}]+)\}/g)
      return matches ? matches.map(match => match.slice(1, -1)) : []
    }

      useEffect(() => {
    // テンプレートが変更されたときに新しいプレースホルダーを検出
    const placeholders = extractPlaceholders(template)
      } ,[template])

  useEffect(() => {
    const text = 
    `
      タイトル "${title}"
      日付 ${date}
      リンク ${link}
    `.trim();
   setResult(text);
  }, [title, date, link]);

  const placeholders = extractPlaceholders(template)


  return (
    <div>
      <h2>テンプレート編集</h2>
        <textarea
          className={styles.field}
          value = {template}
          onChange={(e) => setTemplate(e.target.value)}
          placeholder="テンプレートを入力してください"
        /> <br />

          <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))', gap: '15px' }}>
            {placeholders.map(placeholder => (
              <div key={placeholder}>
                <label style={{ display: 'block', marginBottom: '4px', fontWeight: 'bold' }}>
                  {placeholder}:
                </label>
                <input
                  style={{
                    width: '100%',
                    padding: '8px',
                    border: '1px solid #ddd',
                    borderRadius: '4px'
                  }}
                  value={fields[placeholder] || ''}
                  onChange={(e) => updateFieldValue(placeholder, e.target.value)}
                  placeholder={`${placeholder}を入力`}
                />
              </div>
            ))}
          </div>

        <label>Title: </label><br />
        <input placeholder="{タイトル}を入力" value={title} onChange={(e) => setTitle(e.target.value)} /><br />
        <label>Date: </label><br />
        <input placeholder="日付を入力" value={date} onChange={(e) => setDate(e.target.value)}/><br />
        <label>Link: </label><br />
        <input placeholder="リンクを入力" value={link} onChange={(e) => setLink(e.target.value)} /><br />


        <h3>結果</h3>
        <textarea 
          className={styles.result}
          value={result}
          onChange={(e) => setResult(e.target.value)}
        />
        <br />
        <button onClick={() => navigator.clipboard.writeText(result)}>結果をコピー</button>


    </div>
  );
};

export default TemplateEditor;
