import { useState, useEffect } from 'react'
import styles from './TemplateEditor.module.css'

const TemplateEditor  = () => {

  // テンプレート文
    const [template, setTemplate] = useState('今日の曲のタイトルは"{title}"で、作者は"{author}"です')
    // 動的フィールド（プレースホルダーから自動抽出）
    const [fields, setFields] = useState({})
    const [result, setResult] = useState("")
    /* 
    const [title, setTitle] = useState("")
    const [date, setDate] = useState("")
    const [link, setLink] = useState("")
    */

    // テンプレートからプレースホルダーを抽出
  const extractPlaceholders = (text) => {
    const matches = text.match(/\{([^}]+)\}/g)
    return matches ? matches.map(match => match.slice(1, -1)) : []
  }

    // フィールド値を更新
  const updateFieldValue = (fieldName, value) => {
    setFields(prev => ({
      ...prev,
      [fieldName]: value
    }))
  }

    // テンプレートにフィールド値を適用
  const applyTemplate = (templateText, fieldValues) => {
    let result = templateText
    Object.entries(fieldValues).forEach(([key, value]) => {
      const placeholder = `{${key}}`
      result = result.replaceAll(placeholder, value || `{${key}}`)
    })
    return result
  }

    // プリセットテンプレート
  const presetTemplates = [
    {
      name: "音楽紹介",
      template: '今日の曲のタイトルは"{title}"で、作者は"{author}"です'
    },
    {
      name: "記事紹介", 
      template: 'タイトル: "{title}"\n日付: {date}\nリンク: {url}\n概要: {summary}'
    },
    {
      name: "商品紹介",
      template: '商品名: {product}\n価格: {price}円\n説明: {description}\n購入リンク: {link}'
    },
    {
      name: "イベント告知",
      template: 'イベント名: {event}\n日時: {datetime}\n場所: {location}\n詳細: {details}'
    },
    {
      name: "カスタム",
      template: ''
    }
  ]

    // プリセット選択
  const selectPreset = (preset) => {
    setTemplate(preset.template)
  }

    // フィールドをクリア
  const clearFields = () => {
    setFields({})
  }

    useEffect(() => {
    // テンプレートが変更されたときに新しいプレースホルダーを検出
    const placeholders = extractPlaceholders(template)
    
    // 新しいフィールドを追加（既存のフィールド値は保持）
    const newFields = { ...fields }
    placeholders.forEach(placeholder => {
      if (!(placeholder in newFields)) {
        newFields[placeholder] = ''
      }
    })
    
    // 使われなくなったフィールドを削除
    Object.keys(newFields).forEach(key => {
      if (!placeholders.includes(key)) {
        delete newFields[key]
      }
    })
    
    setFields(newFields)
  }, [template])

    useEffect(() => {
    // テンプレートまたはフィールド値が変更されたときに結果を更新
    setResult(applyTemplate(template, fields))
  }, [template, fields])

  const placeholders = extractPlaceholders(template)


  useEffect(() => {
    const text = 
    `
     タイトル "${title}"
      日付 ${date}
      リンク ${link}
    `.trim();
   setResult(text);
  }, [title, date, link]);


  return (
    <div>
      <h2>テンプレート編集</h2>

      {/* プリセット選択 */}
      <div style={{ marginBottom: '20px' }}>
        <h3>プリセットテンプレート</h3>
        <div style={{ display: 'flex', flexWrap: 'wrap', gap: '10px' }}>
          {presetTemplates.map((preset, index) => (
            <button
              key={index}
              onClick={() => selectPreset(preset)}
              style={{
                padding: '8px 16px',
                border: '1px solid #ddd',
                borderRadius: '4px',
                backgroundColor: '#f5f5f5',
                cursor: 'pointer'
              }}
            >
              {preset.name}
            </button>
          ))}
        </div>
      </div>

            {/* テンプレート編集 */}
      <div style={{ marginBottom: '20px' }}>
        <h3>テンプレート編集</h3>
        <div style={{ marginBottom: '10px' }}>
          <small style={{ color: '#666' }}>
            変数は {`{変数名}`} の形式で記述してください。例: {`{title}`}, {`{author}`}, {`{date}`}
          </small>
        </div>
        <textarea
          style={{
            width: '100%',
            height: '120px',
            padding: '10px',
            fontSize: '14px',
            border: '1px solid #ddd',
            borderRadius: '4px',
            fontFamily: 'monospace'
          }}
          value={template}
          onChange={(e) => setTemplate(e.target.value)}
          placeholder="テンプレートを入力してください..."
        />
      </div>

            {/* 検出されたフィールド */}
      {placeholders.length > 0 && (
        <div style={{ marginBottom: '20px' }}>
          <div style={{ display: 'flex', alignItems: 'center', marginBottom: '10px' }}>
            <h3 style={{ margin: 0, marginRight: '15px' }}>フィールド入力</h3>
            <button
              onClick={clearFields}
              style={{
                backgroundColor: '#ff9800',
                color: 'white',
                border: 'none',
                padding: '6px 12px',
                borderRadius: '4px',
                cursor: 'pointer',
                fontSize: '12px'
              }}
            >
              全クリア
            </button>
          </div>

                    <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))', gap: '15px' }}>
            {placeholders.map(placeholder => (
              <div key={placeholder}>
                <label style={{ display: 'block', marginBottom: '4px', fontWeight: 'bold' }}>
                  {placeholder}:
                </label>
                <input
                  style={{
                    width: '100%',
                    padding: '8px',
                    border: '1px solid #ddd',
                    borderRadius: '4px'
                  }}
                  value={fields[placeholder] || ''}
                  onChange={(e) => updateFieldValue(placeholder, e.target.value)}
                  placeholder={`${placeholder}を入力`}
                />
              </div>
            ))}
          </div>
        </div>
      )}

      {/* 結果表示 */}
      <div>
        <h3>プレビュー結果</h3>
        <div style={{
          backgroundColor: '#f9f9f9',
          border: '1px solid #ddd',
          borderRadius: '4px',
          padding: '15px',
          marginBottom: '10px',
          minHeight: '100px',
          whiteSpace: 'pre-wrap',
          fontFamily: 'system-ui, -apple-system, sans-serif',
          lineHeight: '1.5'
        }}>
          {result || 'テンプレートを入力してください...'}
        </div>
        
        <textarea
          style={{
            width: '100%',
            height: '150px',
            padding: '10px',
            fontFamily: 'monospace',
            fontSize: '14px',
            border: '1px solid #ddd',
            borderRadius: '4px',
            marginBottom: '10px'
          }}
          value={result}
          onChange={(e) => setResult(e.target.value)}
        />
        
        <button
          onClick={() => navigator.clipboard.writeText(result)}
          style={{
            backgroundColor: '#2196F3',
            color: 'white',
            border: 'none',
            padding: '12px 24px',
            borderRadius: '4px',
            cursor: 'pointer',
            fontSize: '14px'
          }}
        >
          結果をコピー
        </button>
      </div>
    </div>
  )
}


/*
        <label>Title: </label><br />
        <input placeholder="タイトルを入力" value={title} onChange={(e) => setTitle(e.target.value)} /><br />
        <label>Date: </label><br />
        <input placeholder="日付を入力" value={date} onChange={(e) => setDate(e.target.value)}/><br />
        <label>Link: </label><br />
        <input placeholder="リンクを入力" value={link} onChange={(e) => setLink(e.target.value)} /><br />


        <h3>結果</h3>
        <textarea 
          className={styles.result}
          value={result}
          onChange={(e) => setResult(e.target.value)}
        />
        <br />
        <button onClick={() => navigator.clipboard.writeText(result)}>結果をコピー</button>


    </div>
  );
}; */

export default TemplateEditor;
