import { useState, useEffect } from 'react'
import styles from './TemplateEditor.module.css'

const TemplateEditor  = () => {

    // Âü∫Êú¨ÁöÑ„Å™Áä∂ÊÖãÁÆ°ÁêÜ
    const [template, setTemplate] = useState('‰ªäÊó•„ÅÆÊõ≤„ÅÆ„Çø„Ç§„Éà„É´„ÅØ"{title}"„Åß„ÄÅ‰ΩúËÄÖ„ÅØ"{author}"„Åß„Åô')
    const [fields, setFields] = useState<Record<string, string>>({})
    const [result, setResult] = useState("")
  
    const [title, setTitle] = useState("")
    const [date, setDate] = useState("")
    const [link, setLink] = useState("")
    //const [result, setResult] = useState("")

    // „ÉÜ„É≥„Éó„É¨„Éº„Éà„Åã„Çâ{Â§âÊï∞}„ÇíË¶ã„Å§„Åë„Çã
    const findVariables = (text : string) => {
        const matches = text.match(/\{([^}]+)\}/g)
      return matches ? matches.map(match => match.slice(1, -1)) : []
    }

    // „Éï„Ç£„Éº„É´„Éâ„ÅÆÂÄ§„ÇíÊõ¥Êñ∞„Åô„ÇãÈñ¢Êï∞
    const updateField = (fieldName : string, value : string) => {
      setFields(prev => ({
        ...prev,
        [fieldName]: value
      }))
    }

    // „ÉÜ„É≥„Éó„É¨„Éº„Éà„Å´ÂÄ§„ÇíÂÖ•„Çå„ÇãÈñ¢Êï∞
    const fillTemplate = (templateText : string, fieldValues : string) => {
      let result = templateText
      Object.entries(fieldValues).forEach(([key, value]) => {
        const placeholder = `{${key}}`
        result = result.replaceAll(placeholder, value || `{${key}}`)
      })
      return result
    }

        // „ÉÜ„É≥„Éó„É¨„Éº„Éà„ÅåÂ§â„Çè„Å£„Åü„Å®„Åç„Å´ÂÆüË°å„Åï„Çå„Çã
    useEffect(() => {
      // Êñ∞„Åó„ÅÑÂ§âÊï∞„ÇíË¶ã„Å§„Åë„Çã
      const variables = findVariables(template)
      
      // Êñ∞„Åó„ÅÑ„Éï„Ç£„Éº„É´„Éâ„Ç™„Éñ„Ç∏„Çß„ÇØ„Éà„Çí‰Ωú„Çã
      const newFields = { ...fields }
      variables.forEach(variable => {
        if (!(variable in newFields)) {
          newFields[variable] = ''
        }
      })
      
      setFields(newFields)
    }, [template])

        // „ÉÜ„É≥„Éó„É¨„Éº„Éà„Åã„Éï„Ç£„Éº„É´„Éâ„ÅåÂ§â„Çè„Å£„Åü„Å®„Åç„Å´ÁµêÊûú„ÇíÊõ¥Êñ∞
    useEffect(() => {
      setResult(fillTemplate(template, fields))
    }, [template, fields])

    const variables = findVariables(template)


  useEffect(() => {
    const text = 
    `
     „Çø„Ç§„Éà„É´ "${title}"
      Êó•‰ªò ${date}
      „É™„É≥„ÇØ ${link}
    `.trim();
   setResult(text);
  }, [title, date, link]);


  return (
    <div>
      {/* Step 1: „ÉÜ„É≥„Éó„É¨„Éº„ÉàÂÖ•Âäõ */}
      <div style={{ marginBottom: '20px' }}>
        <h3>üìù „ÉÜ„É≥„Éó„É¨„Éº„Éà„ÇíÂÖ•Âäõ</h3>
        <p style={{ color: '#666', fontSize: '14px' }}>
          Â§âÊï∞„ÅØ {`{Â§âÊï∞Âêç}`} „ÅÆÂΩ¢Âºè„ÅßÊõ∏„ÅÑ„Å¶„Åè„Å†„Åï„ÅÑ
        </p>
        <textarea
          style={{
            width: '100%',
            height: '100px',
            padding: '10px',
            fontSize: '14px',
            border: '1px solid #ddd',
            borderRadius: '4px'
          }}
          value={template}
          onChange={(e) => setTemplate(e.target.value)}
          placeholder="‰æã: ‰ªäÊó•„ÅÆÊõ≤„ÅØ{title}„Åß„ÄÅ‰ΩúËÄÖ„ÅØ{author}„Åß„Åô"
        />
      </div>

        {/* Step 2: Â§âÊï∞„ÅÆÂÖ•Âäõ„Éï„Ç£„Éº„É´„Éâ */}
      {variables.length > 0 && (
        <div style={{ marginBottom: '20px' }}>
          <h3>‚úèÔ∏è Â§âÊï∞„ÇíÂÖ•Âäõ</h3>
          <p style={{ color: '#666', fontSize: '14px' }}>
            Ë¶ã„Å§„Åã„Å£„ÅüÂ§âÊï∞: {variables.join(', ')}
          </p>
          {variables.map(variable => (
            <div key={variable} style={{ marginBottom: '10px' }}>
              <label style={{ display: 'block', marginBottom: '4px', fontWeight: 'bold' }}>
                {variable}:
              </label>
              <input
                style={{
                  width: '100%',
                  padding: '8px',
                  border: '1px solid #ddd',
                  borderRadius: '4px'
                }}
                value={fields[variable] || ''}
                onChange={(e) => updateField(variable, e.target.value)}
                placeholder={`${variable}„ÇíÂÖ•Âäõ„Åó„Å¶„Åè„Å†„Åï„ÅÑ`}
              />
            </div>
          ))}
        </div>
        )} 


      <h2>„ÉÜ„É≥„Éó„É¨„Éº„ÉàÁ∑®ÈõÜ</h2>
        <label>Title: </label><br />
        <input placeholder="{„Çø„Ç§„Éà„É´}„ÇíÂÖ•Âäõ" value={title} onChange={(e) => setTitle(e.target.value)} /><br />
        <label>Date: </label><br />
        <input placeholder="Êó•‰ªò„ÇíÂÖ•Âäõ" value={date} onChange={(e) => setDate(e.target.value)}/><br />
        <label>Link: </label><br />
        <input placeholder="„É™„É≥„ÇØ„ÇíÂÖ•Âäõ" value={link} onChange={(e) => setLink(e.target.value)} /><br />



        <h3>ÁµêÊûú</h3>
        <textarea 
          className={styles.result}
          value={result}
          onChange={(e) => setResult(e.target.value)}
        />
        <br />
        <button onClick={() => navigator.clipboard.writeText(result)}>ÁµêÊûú„Çí„Ç≥„Éî„Éº</button>


    </div>
  );
};

export default TemplateEditor;
